import { Link } from 'react-router-dom'
import { Card, Breadcrumb, Form, Button, Radio, DatePicker, Select, Table, Tag, Space } from 'antd'
import 'moment/locale/zh-cn'//ÈÖçÁΩÆÊàê‰∏≠Êñá
import locale from 'antd/es/date-picker/locale/zh_CN'//ÈÖçÁΩÆÊàêÂΩìÂú∞ÁöÑËØ≠Ë®Ä-‰∏≠Êñá
import './index.scss'
import { EditOutlined, DeleteOutlined } from '@ant-design/icons'
import img404 from '@/assets/placeholde-error.png'
import { useEffect, useState } from 'react'
import { http } from '@/utils'


const { Option } = Select
const { RangePicker } = DatePicker


const Article = () =>{

//üëáÊ∏≤ÊüìÈ¢ëÈÅìÂàóË°®:
	//Ê≠•È™§‰∏ÄÔºöÂ£∞Êòé‰∏Ä‰∏™ÂºÇÊ≠•ÂáΩÊï∞Ë∞ÉÁî®ÂêéÁ´ØÊé•Âè£
	const loadChannelList = async() =>{
		const ref = await http.get('/channels')//ÂÆö‰πâ‰∏Ä‰∏™ÂèòÈáèÊù•Êé•Êî∂ÂêéÁ´ØÊé•Âè£ url ËøîÂõûÁöÑÊï∞ÊçÆÔºÅ
		setChannelList(ref.data.channels) //‚ö°Ô∏èÁî® hook Êù•ÊääÊï∞ÊçÆÊîæÂÖ• channelList ‰∏≠
	}

	//Ê≠•È™§‰∫åÔºö ÂàóË°®ÁöÑ‰∏¥Êó∂Êï∞ÊçÆ(Áî® setCahnnelList ÊääÊï∞ÊçÆÊîæÂÖ• channelList ‰∏≠) 
	const [channelList, setChannelList] = useState([])

	//Ê≠•È™§‰∏âÔºöÂÆû‰æãÂåñË∞ÉÁî®Êé•Âè£ÁöÑÊñπÊ≥ïÔºåËé∑ÂæóÊï∞ÊçÆ(Ê≥®ÊÑèÔºåuseEffect ËøôÈáåËæπ‰∏çËÉΩÁî® asyncÔºÅ)
	useEffect(()=>{
		loadChannelList()
	},[])



	const onFinish = (values) => {
		console.log(values)
	}


	const columnsTitle = [
		{
			title:'Â∞ÅÈù¢',
			dataIndex: 'cover',
			width:120,
			render:cover=>{
				return <img src={ cover || img404 } width={148} height={98} alt="" style={{borderRadius:12}}/>
			}
		},
		{
			title: 'Ê†áÈ¢ò',
			dataIndex: 'title',
			width: 220
		  },
		  {
			title: 'Áä∂ÊÄÅ',
			dataIndex: 'status',
			render: data => <Tag color="green">ÂÆ°Ê†∏ÈÄöËøá</Tag>
		  },
		  {
			title: 'ÂèëÂ∏ÉÊó∂Èó¥',
			dataIndex: 'pubdate'
		  },
		  {
			title: 'ÈòÖËØªÊï∞',
			dataIndex: 'read_count'
		  },
		  {
			title: 'ËØÑËÆ∫Êï∞',
			dataIndex: 'comment_count'
		  },
		  {
			title: 'ÁÇπËµûÊï∞',
			dataIndex: 'like_count'
		  },
		  {
			title: 'Êìç‰Ωú',
			render: data => {
			  return (
				<Space size="middle">
					{/* ‰∏§‰∏™Êìç‰ΩúÊåâÈíÆ */}
				  <Button type="primary" shape="circle" icon={<EditOutlined />} />
				  <Button
					type="primary"
					danger
					shape="circle"
					icon={<DeleteOutlined />}
				  />
				</Space>
			  )
			}
		  }
	]


	const data = [
		{
			id:'8218',
			comment_count: 0,
			cover: {
			  images:['https://images.unsplash.com/photo-1656215662310-26e0ef491d5d?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=928&q=80'],
			},
			like_count: 0,
			pubdate: '2019-03-11 09:00:00',
			read_count: 2,
			status: 2,
			title: 'wkwebviewÁ¶ªÁ∫øÂåñÂä†ËΩΩh5ËµÑÊ∫êËß£ÂÜ≥ÊñπÊ°à' 
		}
	]


	return (
		<div>
			{/* È°∂ÈÉ®Á≠õÈÄâÂå∫Âüü ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî */}
			<Card 
				// È°∂ÈÉ®ÂØºËà™
				title={
					<Breadcrumb separator=">">
						{/* üçûÈù¢ÂåÖÂ±ëÂØºËà™ */}
						<Breadcrumb.Item>  <Link to="/"> Home </Link>  
						</Breadcrumb.Item>
						<Breadcrumb> Context Management </Breadcrumb>
					</Breadcrumb>
				}
				style={{ margin:"0.75rem", marginBottom:"1.5rem", borderRadius:"1rem"}}
				>

				{/* ËøáÊª§È°π */}
				<Form  onFinish={onFinish} initialValues={{status:null}}>
					<Form.Item babel="Áä∂ÊÄÅ" name="status" style={{marginLeft:"0.75rem"}}>
						<Radio.Group>
							<Radio value={null}>All</Radio>
							<Radio value={0}>Draft</Radio>
							<Radio value={1}>Pending</Radio>
							<Radio value={2}>Approved</Radio>
							<Radio value={3}>Rejection</Radio>
						</Radio.Group>
					</Form.Item>

					{/* ‰∏ãÊãâÈÄâÊã©Âô® */}
					<Form.Item label="Channel" name="channel_id" style={{marginLeft:"0.75rem"}} >
						<Select placeholder="Select article channel" style={{width:'18rem'}}>
							{/* Ê≠•È™§ÂõõÔºöÊ∏≤ÊüìÁ≠õÈÄâ‰∏ãÊãâËèúÂçïÁöÑÊï∞ÊçÆ */}
							{channelList.map( channel =>
								<Option value={channel.id} key={channel.id}> {channel.name} </Option>
							)}
						</Select>
					</Form.Item>

					{/*  */}
					<Form.Item label="Date" name="date" style={{marginLeft:"0.75rem"}}>
						{/* ‰º†ÂÖ• locale ËØ≠Ë®ÄÂ±ûÊÄß */}
						<RangePicker locale={locale}></RangePicker>
					</Form.Item>

					<Form.Item>
						<Button type="primary" htmlType="submit" style={{marginLeft:"0.75rem"}}>Á≠õÈÄâ</Button>
					</Form.Item>
				{/* Ê≥®ÊÑèÔºåË¶ÅÁî®‰∏™ÊÄªÁöÑ Form Êù•ÂåÖË£πÂ≠êÈõÜÔºÅ */}
				</Form>
			</Card>


			{/* ÊñáÁ´†ÂàóË°®Âå∫Âüü ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî*/}
			<Card title={`Ê†πÊçÆÁ≠õÈÄâÊù°‰ª∂ÂÖ±Êü•ËØ¢Âà∞ count Êù°ÁªìÊûúÔºö`} style={{borderRadius:"1rem"}}>
        		<Table 
					rowKey="id" 
					columns={columnsTitle}  //Ë°®Ê†ºÁöÑÊï¥‰ΩìËß£ÊûÑÊï∞ÊçÆ
					dataSource={data} 	//ÊñáÁ´†ÁöÑ‰ø°ÊÅØÊï∞ÊçÆ
					/>   
      		</Card>
		</div>
	)
}

export default Article
