import './index.scss'
import * as echarts from './echarts' //表示所有的导出都会放在 echarts 这个对象上



//思路：
//1.把 echarts 加入项目(需要在 react 中获取 DOM 元素, 用 useRef 方法获取，在 useEffect 时机去渲染 DOM 节点)
//2.先不抽离定制化的参数，把最小化的 demo 跑起来
//3.按照需求，抽象出自定义的参数




const Home = () => {

	const chartInit = () => {

		// 基于准备好的dom，初始化echarts实例
		var myChart = echarts.init(document.getElementById('main'));
		// 绘制图表
		myChart.setOption({
		title: {
			text: 'ECharts 入门示例'
		},
		tooltip: {},
		xAxis: {
			data: ['衬衫', '羊毛衫', '雪纺衫', '裤子', '高跟鞋', '袜子']
		},
		yAxis: {},
		series: [
			{
			name: '销量',
			type: 'bar',
			data: [5, 20, 36, 10, 10, 20]
			}
		]
		});
	}

	return( 
		<div>Home</div>
	)
}

export default Home
